import React, { Component, useState } from 'react';
import { StyleSheet, Text, View, Button, Alert, TextInput } from 'react-native';

const fErmatFactorization = number => {

  if ((number <= 0) || !(Number.isInteger(number))) {
      return;
  };

  if ((number % 2) === 0) {
      return [number / 2, 2];
  };

  let a = Math.ceil(Math.sqrt(number));

  if (a**2 === number) {
      return [a, a];
  };

  let b;

  while (true) {
      let b1 = a**2 - number;

      b = Math.sqrt(b1) | 0;

      if (b**2 === b1) {
          break;
      } else {
          a++;
      };
  };

  return [a - b, a + b];
};

export default function App() {
  const [number, setNumber] = useState("0");
  const [answer, setAnswer] = useState(0);
  console.log(number)
  const handleChange = (event = {}) => {
    const name = event.target && event.target.name;
    const value = event.target && event.target.value;
    setNumber(value);
  };
  return (
    <View style={styles.container}>
      <Text style={''}>Fermat's Factorization Method</Text>
      <TextInput label="Set Number:" placeholder="Set Number" onChangeText={text => setNumber(+text)}></TextInput>
      <Button title="Number Factorization" onPress={() => setAnswer(fErmatFactorization(number))}></Button>
      <Text>{answer && `${answer[0]} ${answer[1]}`}</Text>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  }
});
